import React, { useState } from 'react';
import './App.css';
import Sidebar from './components/Sidebar.js';
import ChatInterface from './components/ChatInterface.js';

function App() {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [isFirstMessageSent, setIsFirstMessageSent] = useState(false);
  const [selectedManual, setSelectedManual] = useState(null); // Store selected manual for AI chat

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const handleFirstMessageSent = () => {
    setIsFirstMessageSent(true);
  };

  const handleSelectManual = (manual) => {
    setSelectedManual(manual); // Store the selected manual for reference in the chat
  };

  return (
    <div className="App">
      <Sidebar 
        isOpen={isSidebarOpen} 
        toggleSidebar={toggleSidebar} 
        onManualSelect={handleSelectManual} // Pass manual selection handler
      />
      <div className={`main-content ${isSidebarOpen ? 'blur-background' : ''}`}>
        <h1 className="chat-heading">Guided Operations</h1>
        {!isFirstMessageSent && (
          <div className="welcome-message">
            <span className="highlighted-word">Welcome to Guided Operations!</span>
            <p>
              Speak the solution into existence. Elevate your workflow with guided operations,
              where intelligent, real-time assistance transforms challenges into streamlined success.
            </p>
          </div>
        )}
        <ChatInterface 
          onFirstMessageSent={handleFirstMessageSent} 
          selectedManual={selectedManual} // Pass selected manual to chat interface
        />
      </div>
    </div>
  );
}

export default App;
