import React, { useEffect, useRef } from 'react';
import '../styles/Sidebar.css';
import burgerIcon from '../assets/icons/burger.png'; // Import your burger menu icon

function Sidebar({ isOpen, toggleSidebar }) {
  const sidebarRef = useRef(null);

  useEffect(() => {
    function handleClickOutside(event) {
      // Check if the click was outside the sidebar and if the sidebar is open
      if (sidebarRef.current && !sidebarRef.current.contains(event.target) && isOpen) {
        toggleSidebar();
      }
    }

    document.addEventListener('mousedown', handleClickOutside);

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [isOpen, toggleSidebar]);

  return (
    <div className={`sidebar-container ${isOpen ? 'open' : ''}`}>
      {!isOpen && (
        <div className="sidebar-strip" onClick={toggleSidebar}>
          <img src={burgerIcon} alt="Open Sidebar" className="burger-icon" />
        </div>
      )}
      <div
        className={`sidebar ${isOpen ? 'open' : ''}`}
        ref={sidebarRef}
      >
        {isOpen && (
          <div className="sidebar-content">
            <h2>Sidebar</h2>
            <p>This is the sidebar content.</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default Sidebar;
