import React, { useState } from 'react';
import '../styles/ChatInterface.css';

const ChatInterface = () => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [aiResponse, setAiResponse] = useState(''); // State for AI response

  const handleSendMessage = () => {
    if (inputMessage.trim() !== '') {
      setMessages([...messages, { text: inputMessage, sender: 'user' }]);

      // Simulating an AI response
      const response = `AI response to: ${inputMessage}`;
      setAiResponse(response); // Set the AI response

      setInputMessage('');
      // Here you would typically send the message to a backend or chat service
    }
  };

  return (
    <div className="chat-interface">
        <div className="qqq">Hey bhavani make it more</div>
      <div className="message-input">
        <input
          type="text"
          value={inputMessage}
          onChange={(e) => setInputMessage(e.target.value)}
          placeholder="Type a message..."
        />
        <button onClick={handleSendMessage}>Send</button>
      </div>

      {/* Display AI response below the input */}
      <div className="ai-response">
        <p>{aiResponse}</p>
      </div>
    </div>
  );
};

export default ChatInterface;
