import React, { useState } from 'react';
import { Box, Button, Typography } from '@mui/material';
import micIcon from '../assets/icons/mic.png';
import sendIcon from '../assets/icons/send.png';
import '../styles/ChatInterface.css';

const backendUrl = process.env.REACT_APP_BACKEND_URL;

const ChatInterface = ({ onFirstMessageSent, selectedManual }) => {
  const [inputMessage, setInputMessage] = useState('');
  const [aiResponse, setAiResponse] = useState('');
  const [loading, setLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [tokenUsage, setTokenUsage] = useState(null); // State for token usage

  const handleSendMessage = async () => {
    if (inputMessage.trim() !== '') {
      if (!selectedManual) {
        setErrorMessage('Please select a manual first.');
        return;
      }
      setLoading(true);
      setErrorMessage('');

      try {
        const response = await fetch(`${backendUrl}/api/chat`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            user_input: inputMessage,
            selected_xml: selectedManual,
            model: 'claude-v1',
            max_tokens_to_sample: 300,
          }),
        });

        if (!response.ok) {
          throw new Error('Error fetching AI response');
        }

        const data = await response.json();
        setAiResponse(data.response || 'No response from the AI.');
        setTokenUsage(data.token_usage); // Set token usage from the response
      } catch (error) {
        console.error('Error fetching AI response:', error);
        setErrorMessage('Error fetching AI response. Please try again later.');
      } finally {
        setLoading(false);
        setInputMessage('');
      }
    }
  };

  return (
    <div className="chat-interface">
      <div className="message-input">
        <div className="input-wrapper">
          <input
            type="text"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            placeholder="Enter your prompt here..."
          />
          <img
            src={micIcon}
            alt="Microphone"
            className="mic-icon"
            onClick={() => console.log('Microphone clicked')}
            aria-label="Activate voice input"
          />
          <img
            src={sendIcon}
            alt="Send"
            className="send-icon"
            onClick={handleSendMessage}
            style={{ opacity: inputMessage.trim() === '' ? 0.5 : 1 }}
          />
        </div>
      </div>

      {loading && <p>Generating response...</p>}
      {aiResponse && <p>{aiResponse}</p>}
      {errorMessage && <p className="error-message">{errorMessage}</p>}

      {/* Display token usage if available */}
      {tokenUsage && (
        <div className="token-usage">
          <p>Input Tokens: {tokenUsage.input_tokens}</p>
          <p>Output Tokens: {tokenUsage.output_tokens}</p>
          <p>Total Tokens: {tokenUsage.total_tokens}</p>
        </div>
      )}
    </div>
  );
};

export default ChatInterface;
