import React, { useState } from 'react';
import './App.css';
import Sidebar from './components/Sidebar.js';
import ChatInterface from './components/ChatInterface.js';

function App() {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [isMessageSent, setIsMessageSent] = useState(false); // New state

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  // Callback function to trigger the removal of the welcome message
  const handleFirstMessageSent = () => {
    setIsMessageSent(true);
  };

  return (
    <div className="App">
      <Sidebar isOpen={isSidebarOpen} toggleSidebar={toggleSidebar} />
      <div className={`main-content ${isSidebarOpen ? 'blur-background' : ''}`}>
        <h1 className="chat-heading">Guided Operations</h1>

        {/* Conditionally render the welcome message */}
        {!isMessageSent && (
          <div className="welcome-message">
            <span className="highlighted-word">Welcome to Guided Operations!</span>
            <p>
              Speak the solution into existence. Elevate your workflow with guided operations,
              where intelligent, real-time assistance transforms challenges into streamlined success.
            </p>
          </div>
        )}

        {/* Pass the callback to the ChatInterface component */}
        <ChatInterface onFirstMessageSent={handleFirstMessageSent} />
      </div>
    </div>
  );
}

export default App;
