import React, { useEffect, useRef, useState } from 'react';
import '../styles/Sidebar.css';

function Sidebar({ isOpen, toggleSidebar }) {
  const sidebarRef = useRef(null);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false); // State for dropdown toggle
  const [selectedFile, setSelectedFile] = useState(null); // State for the uploaded file

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  useEffect(() => {
    function handleClickOutside(event) {
      // Check if the click was outside the sidebar and if the sidebar is open
      if (sidebarRef.current && !sidebarRef.current.contains(event.target) && isOpen) {
        toggleSidebar();
      }
    }

    document.addEventListener('mousedown', handleClickOutside);

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [isOpen, toggleSidebar]);

  return (
    <div
      className={`sidebar ${isOpen ? 'open' : ''}`}
      ref={sidebarRef}
      onClick={!isOpen ? toggleSidebar : null}
    >
      <div className="sidebar-content">
        {isOpen && (
          <>
            <h2 className="sidebar-heading">Upload PDF</h2>

            {/* Instructions Dropdown */}
            <div className="dropdown-wrapper">
              <div className="dropdown-header" onClick={toggleDropdown}>
                <span>Instructions</span>
                <span className="dropdown-arrow">{isDropdownOpen ? '▲' : '▼'}</span>
              </div>
              {isDropdownOpen && (
                <div className="dropdown-content">
                  <p>Upload a PDF file to convert it into an XML file.</p>
                </div>
              )}
            </div>

            {/* File Upload Section */}
            <div className="upload-section">
              <p className="upload-label">Choose a PDF file</p>
              <label className="file-upload-area">
                <input
                  type="file"
                  accept="application/pdf"
                  onChange={handleFileChange}
                  style={{ display: 'none' }}
                />
                <div className="file-upload-placeholder">
                  <p>Browse files</p>
                  <p>Drag and drop file here</p>
                  <p>Limit 200MB per file. PDF only</p>
                </div>
              </label>
              {selectedFile && <p className="file-name">Selected File: {selectedFile.name}</p>}
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default Sidebar;
