import React, { useState } from 'react';
import '../styles/ChatInterface.css';
import micIcon from '../src/assets/mic.png';

const ChatInterface = () => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [aiResponse, setAiResponse] = useState(''); // State for AI response

  const handleSendMessage = async () => {
    if (inputMessage.trim() !== '') {
      setMessages([...messages, { text: inputMessage, sender: 'user' }]);

      // Call Claude API to get a response
      const response = await getClaudeResponse(inputMessage);
      setAiResponse(response); // Set the AI response

      setInputMessage('');
    }
  };

  // Function to get AI response from Claude API
  const getClaudeResponse = async (message) => {
    try {
      const response = await fetch('http://localhost:5000/api/claude', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          prompt: message,
          model: 'claude-v1',
          max_tokens_to_sample: 300,
        }),
      });


      const data = await response.json();
      return data.completion; // Get the AI response from API data
    } catch (error) {
      console.error('Error fetching AI response:', error);
      return 'Failed to get AI response. Please try again.';
    }
  };

  // Placeholder function for handling microphone clicks
  const handleMicrophoneClick = () => {
    console.log('Microphone clicked');
    // TODO: Implement voice input functionality
  };

  return (
    <div className="chat-interface">
      <p className="question">What can I help you with?</p>
      <div className="message-input">
        <input
          type="text"
          value={inputMessage}
          onChange={(e) => setInputMessage(e.target.value)}
          placeholder="Enter your prompt here..."
        />
        <button className="mic-button" onClick={handleMicrophoneClick}>
          <img src = "mic.png" alt="mic"></img>
        </button>
        <button onClick={handleSendMessage}>Send</button>
      </div>

      {/* Conditionally render AI response only when it's not empty */}
      {aiResponse && (
        <div className="ai-response">
          <p>{aiResponse}</p>
        </div>
      )}
    </div>
  );
};

export default ChatInterface;
