import React, { useState } from 'react';
import { Box, Button, Typography } from '@mui/material';
import '../styles/ChatInterface.css';
import micIcon from '../assets/icons/mic.png';
import sendIcon from '../assets/icons/send.png';

// Get backend URL from environment variables
const backendUrl = process.env.REACT_APP_BACKEND_URL;

const QuickSuggestions = ({ onSuggestionClick }) => {
  const suggestions = [
    { main: 'How do I start', description: '[specific task]?' },
    { main: 'Can you guide me through', description: '[process]?' },
    { main: 'How do I fix', description: '[issue/error]?' }
  ];

  return (
    <Box sx={{ display: 'flex', justifyContent: 'center', mt: 2, mb: 2, flexWrap: 'wrap' }}>
      {suggestions.map((suggestion) => (
        <Button
          key={suggestion.main}
          variant="contained"
          onClick={() => onSuggestionClick(suggestion.main)}
          sx={{
            mx: 1,
            my: 1,
            color: '#FFFFFF',
            backgroundColor: '#284F7D',
            fontFamily: 'Urbanist, sans-serif',
            borderRadius: '10px',
            padding: '5px 60px',
            textTransform: 'none',
            boxShadow: '4px 4px 4px rgba(0, 0, 0, 0.25)',
            border: 'none',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            '&:hover': {
              backgroundColor: '#3A7BC8',
              boxShadow: 'none',
            }
          }}
        >
          <Typography sx={{ fontSize: '21px', fontWeight: 600, fontFamily: 'Urbanist, sans-serif' }}>
            {suggestion.main}
          </Typography>
          <Typography sx={{ fontSize: '16px', fontWeight: 400, opacity: 0.8, fontFamily: 'Urbanist, sans-serif' }}>
            {suggestion.description}
          </Typography>
        </Button>
      ))}
    </Box>
  );
};

const ChatInterface = ({ onFirstMessageSent }) => {
  const [inputMessage, setInputMessage] = useState('');
  const [aiResponse, setAiResponse] = useState('');
  const [isFirstMessageSent, setIsFirstMessageSent] = useState(false);
  const [loading, setLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');

  const handleSendMessage = async () => {
    if (inputMessage.trim() !== '') {
      if (!isFirstMessageSent) {
        setIsFirstMessageSent(true);
        onFirstMessageSent();
      }

      try {
        setLoading(true);
        setErrorMessage(''); // Clear previous error message
        const response = await getClaudeResponse(inputMessage);
        setAiResponse(response);
      } catch (error) {
        console.error('Error fetching AI response:', error);
        setErrorMessage('Error fetching AI response. Please try again later.');
      } finally {
        setInputMessage(''); // Clear input field after sending message
        setLoading(false);
      }
    }
  };

  const getClaudeResponse = async (message) => {
    try {
      const response = await fetch(`${backendUrl}/api/chat`, {  // Use the backend URL from env
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          user_input: message,
          model: 'claude-v1',
          max_tokens_to_sample: 300,
        }),
      });

      if (!response.ok) {
        throw new Error('Error in fetching response from server.');
      }

      const data = await response.json();
      return data.response || data.completion;  // Return appropriate response field
    } catch (error) {
      console.error('Error fetching AI response:', error);
      throw error;
    }
  };

  const handleMicrophoneClick = () => {
    console.log('Microphone clicked');
    // TODO: Implement voice input functionality
  };

  const handleSuggestionClick = (suggestion) => {
    setInputMessage(suggestion);
  };

  return (
    <div className={`chat-interface ${isFirstMessageSent ? 'chat-interface-active' : ''}`}>
      {!isFirstMessageSent && <p className="question">What can I help you with?</p>}

      <div className={`message-input ${isFirstMessageSent ? 'message-input-bottom' : ''}`}>
        <div className="input-wrapper">
          <input
            type="text"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            placeholder="Enter your prompt here..."
          />
          <img
            src={micIcon}
            alt="Microphone"
            className="mic-icon"
            onClick={handleMicrophoneClick}
            aria-label="Activate voice input"
          />
          <img
            src={sendIcon}
            alt="Send"
            className="send-icon" 
            onClick={handleSendMessage}
            aria-label="Send message"
            style={{ opacity: inputMessage.trim() === '' ? 0.5 : 1 }}
            disabled={inputMessage.trim() === ''}
          />
        </div>
      </div>

      {!isFirstMessageSent && <QuickSuggestions onSuggestionClick={handleSuggestionClick} />}

      {loading && (
        <div className="loading">
          <p>Generating...</p>
        </div>
      )}

      {/* Display AI response */}
      {aiResponse && (
        <div className={`ai-response ${!loading ? 'active' : ''}`}>
          <p>{aiResponse}</p>
        </div>
      )}

      {/* Display error message if there's an error */}
      {errorMessage && (
        <div className="error-message">
          <p>{errorMessage}</p>
        </div>
      )}
    </div>
  );
};

export default ChatInterface;
