import React, { useState } from 'react'; // Add useState here
import './App.css';
import Sidebar from './components/Sidebar';
import ChatInterface from './components/ChatInterface';

function App() {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  return (
    <div className="App">
      <Sidebar isOpen={isSidebarOpen} toggleSidebar={toggleSidebar} />
      <h1 className="chat-heading">Guided Operations</h1>
      <div className="welcome-message">
          <span className="highlighted-word">Welcome to Guided Operations !</span>
          <p>Speak the solution into existence. Elevate your workflow with guided operations, where intelligent, real-time assistance transforms challenges into streamlined success</p>
      </div>
      <p>What can I help you with?</p>
      <ChatInterface />
      <div className={`main-content ${isSidebarOpen ? 'blur-background' : ''}`}>
        {/* Main content goes here */}
      </div>
    </div>
  );
}

export default App;
