import React, { useEffect, useRef, useState } from 'react';
import '../styles/Sidebar.css';

function Sidebar({ isOpen, toggleSidebar }) {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false); // State for dropdown toggle
  const sidebarRef = useRef(null);

  // Toggle dropdown open/close
  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  useEffect(() => {
    function handleClickOutside(event) {
      if (sidebarRef.current && !sidebarRef.current.contains(event.target) && isOpen) {
        toggleSidebar();
      }
    }

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [isOpen, toggleSidebar]);

  return (
    <div
      className={`sidebar ${isOpen ? 'open' : ''}`}
      ref={sidebarRef}
      onClick={!isOpen ? toggleSidebar : null}
    >
      <div className="sidebar-content">
        {isOpen && (
          <>
            <h2 className='sidebar-heading' onClick={toggleDropdown}>
              Upload PDF
            </h2>

            {/* Dropdown content */}
            {isDropdownOpen && (
              <div className="dropdown">
                <p className="instruction-text">
                  Upload a PDF file to convert it into an XML file.
                </p>
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
}

export default Sidebar;
