import React, { useState } from 'react';
import '../styles/ChatInterface.css';
import micIcon from '../assets/icons/mic.png';
import sendIcon from '../assets/icons/send.png';

const ChatInterface = ({ onFirstMessageSent, selectedManual }) => {
  const [inputMessage, setInputMessage] = useState('');
  const [aiResponse, setAiResponse] = useState('');
  const [isFirstMessageSent, setIsFirstMessageSent] = useState(false);
  const [loading, setLoading] = useState(false);

  const handleSendMessage = async () => {
    if (inputMessage.trim() !== '') {
      if (!isFirstMessageSent) {
        setIsFirstMessageSent(true);
        onFirstMessageSent();
      }

      try {
        setLoading(true);
        const response = await getClaudeResponse(inputMessage);
        setAiResponse(response);
      } catch (error) {
        setAiResponse('Error fetching AI response. Please try again later.');
      } finally {
        setInputMessage('');
        setLoading(false);
      }
    }
  };

  const getClaudeResponse = async (message) => {
    try {
      const response = await fetch('/api/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          user_input: message,
          selected_xml: selectedManual // Pass the selected manual
        }),
      });

      const data = await response.json();
      return data.response; 
    } catch (error) {
      console.error('Error fetching AI response:', error);
      throw error;
    }
  };

  return (
    <div className={`chat-interface ${isFirstMessageSent ? 'chat-interface-active' : ''}`}>
      <div className="message-input">
        <div className="input-wrapper">
          <input
            type="text"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            placeholder="Enter your prompt here..."
          />
          <img
            src={micIcon}
            alt="Microphone"
            className="mic-icon"
          />
          <img
            src={sendIcon}
            alt="Send"
            className="send-icon" 
            onClick={handleSendMessage}
          />
        </div>
      </div>

      {loading && (
        <div className="loading">
          <p>Loading AI response...</p>
        </div>
      )}

      {aiResponse && (
        <div className="ai-response">
          <p>{aiResponse}</p>
        </div>
      )}
    </div>
  );
};

export default ChatInterface;
