import React, { useState } from 'react';
import { Box, Button } from '@mui/material';
import '../styles/ChatInterface.css';
import micIcon from '../assets/icons/mic.png';
import sendIcon from '../assets/icons/send.png';

const QuickSuggestions = ({ onSuggestionClick }) => {
  const suggestions = [
    'How do i start ?',
    'Can you guide me through ?',
    'How do i fix?'
  ];

  return (
    <Box sx={{ display: 'flex', justifyContent: 'center', mt: 2, mb: 2, flexWrap: 'wrap' }}>
      {suggestions.map((suggestion) => (
        <Button
          key={suggestion}
          variant="outlined"
          onClick={() => onSuggestionClick(suggestion)}
          sx={{
            mx: 1,
            my: 1,
            color: '#E0E0E0',
            backgroundColor: '#284F7D',
            fontSize: '20px',
            fontFamily: 'Urbanist',
            fontWeight: 800,
            boxShadow: '4px 4px 4px rgba(0, 0, 0, 0.5)',
            textTransform: 'none',
            '&:hover': {
              backgroundColor: '#1E3A5F',
              borderColor: '#E0E0E0',
            }
          }}
        >
          {suggestion}
        </Button>
      ))}
    </Box>
  );
}

const ChatInterface = ({ onFirstMessageSent }) => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [aiResponse, setAiResponse] = useState('');
  const [isFirstMessageSent, setIsFirstMessageSent] = useState(false);
  const [loading, setLoading] = useState(false);
  const [xmlContent, setXmlContent] = useState(null);

  const handleSendMessage = async () => {
    if (inputMessage.trim() !== '') {
      setMessages((prevMessages) => [...prevMessages, { text: inputMessage, sender: 'user' }]);

      if (!isFirstMessageSent) {
        setIsFirstMessageSent(true);
        onFirstMessageSent(); // Call the prop function to notify App.js
      }

      try {
        setLoading(true);
        const response = await getClaudeResponse(inputMessage);
        setAiResponse(response);
        setMessages((prevMessages) => [...prevMessages, { text: response, sender: 'ai' }]);
      } catch (error) {
        setAiResponse('Error fetching AI response. Please try again later.');
        setMessages((prevMessages) => [...prevMessages, { text: 'Error fetching AI response. Please try again later.', sender: 'ai' }]);
      } finally {
        setInputMessage('');
        setLoading(false);
      }
    }
  };

  const getClaudeResponse = async (message) => {
    try {
      const response = await fetch('http://localhost:5000/api/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          prompt: message,
          xml_content: xmlContent,
        }),
      });

      const data = await response.json();
      return data.response;
    } catch (error) {
      console.error('Error fetching AI response:', error);
      throw error;
    }
  };

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (file) {
      const formData = new FormData();
      formData.append('file', file);

      try {
        setLoading(true);
        const response = await fetch('http://localhost:5000/api/process_pdf', {
          method: 'POST',
          body: formData,
        });

        const data = await response.json();
        setXmlContent(data.xml_content);
        setAiResponse('PDF processed successfully. You can now ask questions about its content.');
        setMessages((prevMessages) => [...prevMessages, { text: 'PDF processed successfully. You can now ask questions about its content.', sender: 'ai' }]);
      } catch (error) {
        console.error('Error processing PDF:', error);
        setAiResponse('Error processing PDF. Please try again.');
        setMessages((prevMessages) => [...prevMessages, { text: 'Error processing PDF. Please try again.', sender: 'ai' }]);
      } finally {
        setLoading(false);
      }
    }
  };

  const handleMicrophoneClick = () => {
    console.log('Microphone clicked');
    // TODO: Implement voice input functionality
  };

  const handleSuggestionClick = (suggestion) => {
    setInputMessage(suggestion);
  };

  return (
    <div className={`chat-interface ${isFirstMessageSent ? 'chat-interface-active' : ''}`}>
      {!isFirstMessageSent && <p className="question">What can I help you with?</p>}

      <div className={`message-input ${isFirstMessageSent ? 'message-input-bottom' : ''}`}>
        <div className="input-wrapper">
          <input
            type="text"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            placeholder="Enter your prompt here..."
          />
          <img
            src={micIcon}
            alt="Microphone"
            className="mic-icon"
            onClick={handleMicrophoneClick}
            aria-label="Activate voice input"
          />
          <img
            src={sendIcon}
            alt="Send"
            className="send-icon" 
            onClick={handleSendMessage}
            aria-label="Send message"
            style={{ opacity: inputMessage.trim() === '' ? 0.5 : 1 }}
          />
        </div>
      </div>

      {!isFirstMessageSent && <QuickSuggestions onSuggestionClick={handleSuggestionClick} />}

      <div className="file-upload">
        <input
          type="file"
          accept=".pdf"
          onChange={handleFileUpload}
          id="pdf-upload"
          style={{ display: 'none' }}
        />
        <label htmlFor="pdf-upload" className="upload-button">
          Upload PDF
        </label>
      </div>

      {loading && (
        <div className="loading">
          <p>Loading AI response...</p>
        </div>
      )}

      <div className="message-container">
        {messages.map((message, index) => (
          <div key={index} className={`message ${message.sender}`}>
            <p>{message.text}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ChatInterface;
