import React, { useEffect, useRef, useState } from 'react';
import '../styles/Sidebar.css';
import burgerIcon from '../assets/icons/burger.png';
import settingIcon from '../assets/icons/settings.png';

// Get backend URL from environment variables
const backendUrl = process.env.REACT_APP_BACKEND_URL;

function Sidebar({ isOpen, toggleSidebar }) {
  const sidebarRef = useRef(null);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploadStatus, setUploadStatus] = useState('');
  const [manuals, setManuals] = useState([]); // New state for manuals
  const [selectedManual, setSelectedManual] = useState(''); // New state for selected manual

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file && file.type === 'application/pdf') {
      setSelectedFile(file);
      setUploadStatus(''); // Reset upload status on file change
    } else {
      setUploadStatus('Please select a PDF file.');
    }
  };

  const handleFileUpload = async () => {
    if (!selectedFile) return;

    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const response = await fetch(`${backendUrl}/api/process_pdf`, {  // Use backend URL from env
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        const result = await response.json();
        console.log('Upload response:', result);
        setUploadStatus('File uploaded successfully!');
        // You can further handle the result (like showing the result in the UI)
      } else {
        console.error('Error uploading file');
        setUploadStatus('Failed to upload the file.');
      }
    } catch (error) {
      console.error('Error uploading file:', error);
      setUploadStatus('Error uploading file. Please try again.');
    }
  };

  useEffect(() => {
    function handleClickOutside(event) {
      if (sidebarRef.current && !sidebarRef.current.contains(event.target) && isOpen) {
        toggleSidebar(); // Close the sidebar when clicking outside
      }
    }

    document.addEventListener('mousedown', handleClickOutside);

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [isOpen, toggleSidebar]);

  // New useEffect to fetch manuals from backend
  useEffect(() => {
    const fetchManuals = async () => {
      try {
        const response = await fetch(`${backendUrl}/api/list_xml_files`, {
          method: 'GET',
          headers: {
              'Content-Type': 'application/json',
          },
      });
        const data = await response.json();
        setManuals(data.xml_files);
      } catch (error) {
        console.error('Error fetching manuals:', error);
      }
    };

    fetchManuals();
  }, []);

  return (
    <div className={`sidebar ${isOpen ? 'open' : ''}`} ref={sidebarRef}>
      <div className="sidebar-toggle" onClick={toggleSidebar}>
        <img src={burgerIcon} alt="Toggle Sidebar" className="burger-icon" />
      </div>
      <div className="sidebar-toggle" onClick={toggleSidebar}>
        <img src={settingIcon} alt="Setting Sidebar" className="setting-icon" />
      </div>
      <div className="sidebar-content">
        {isOpen && (
          <>
            <h2 className="sidebar-heading">Upload PDF</h2>
            <div className="dropdown-wrapper">
              <div className="dropdown-header" onClick={toggleDropdown}>
                <span>Instructions</span>
                <span className="dropdown-arrow">{isDropdownOpen ? '▲' : '▼'}</span>
              </div>
              {isDropdownOpen && (
                <div className="dropdown-content">
                  <p>Upload a PDF file to convert it into an XML file.</p>
                </div>
              )}
            </div>
            <div className="upload-section">
              <p className="upload-label">Choose a PDF file</p>
              <label className="file-upload-area">
                <input
                  type="file"
                  accept="application/pdf"
                  onChange={handleFileChange}
                  style={{ display: 'none' }}
                />
                <div className="file-upload-placeholder">
                  <p>Browse files</p>
                  <p>Drag and drop file here</p>
                  <p>Limit 200MB per file. PDF only</p>
                </div>
              </label>
              {selectedFile && (
                <>
                  <p className="file-name">Selected File: {selectedFile.name}</p>
                  <button onClick={handleFileUpload}>Upload PDF</button>
                </>
              )}
              {uploadStatus && (
                <p className={`upload-status ${uploadStatus.includes('success') ? 'success' : 'error'}`}>
                  {uploadStatus}
                </p>
              )}
            </div>
            <div className="manual-select-section">
              <p className="manual-select-label">Select Manual to Reference</p>
              <select 
                value={selectedManual} 
                onChange={(e) => setSelectedManual(e.target.value)}
                className="manual-select"
              >
                <option value="">Select a manual</option>
                {manuals.map((manual, index) => (
                  <option key={index} value={manual}>{manual}</option>
                ))}
              </select>
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default Sidebar;
