import React, { useState } from 'react';
import { Box, Button } from '@mui/material';
import '../styles/ChatInterface.css';
import micIcon from '../assets/icons/mic.png';
import sendIcon from '../assets/icons/send.png';

const QuickSuggestions = ({ onSuggestionClick }) => {
  const suggestions = [
    'how do i start ?',
    'can you guide me through ?',
    'how do i fix?'
  ];

  return (
    <Box sx={{ display: 'flex', justifyContent: 'center', mt: 2, mb: 2, flexWrap: 'wrap' }}>
      {suggestions.map((suggestion, index) => (
        <Button 
          key={index} 
          variant="outlined" 
          onClick={() => onSuggestionClick(suggestion)}
          sx={{ 
            mx: 1, 
            my: 1, 
            color: '#E0E0E0',
            backgroundColor: '#284F7D',
            fontSize: '20px',
            fontFamily: 'Urbanist',
            fontWeight: 500,
            boxShadow: '4px 4px 4px rgba(0, 0, 0, 0.5)',
            '&:hover': {
              backgroundColor: '#1E3A5F',
              borderColor: '#E0E0E0',
            }
          }}
        >
          {suggestion}
        </Button>
      ))}
    </Box>
  );
};

const ChatInterface = ({ onFirstMessageSent }) => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [aiResponse, setAiResponse] = useState('');

  const handleSendMessage = async () => {
    if (inputMessage.trim() !== '') {
      setMessages([...messages, { text: inputMessage, sender: 'user' }]);

      // Call Claude API to get a response
      const response = await getClaudeResponse(inputMessage);
      setAiResponse(response);

      setInputMessage('');

      // Trigger the callback to notify that the first message is sent
      if (messages.length === 0) {
        onFirstMessageSent();
      }
    }
  };

  const getClaudeResponse = async (message) => {
    try {
      const response = await fetch('http://localhost:5000/api/claude', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          prompt: message,
          model: 'claude-v1',
          max_tokens_to_sample: 300,
        }),
      });

      const data = await response.json();
      return data.completion;
    } catch (error) {
      console.error('Error fetching AI response:', error);
      return 'Failed to get AI response. Please try again.';
    }
  };

  const handleMicrophoneClick = () => {
    console.log('Microphone clicked');
    // TODO: Implement voice input functionality
  };

  const handleSuggestionClick = (suggestion) => {
    setInputMessage(suggestion);
  };

  return (
    <div className="chat-interface">
      <p className="question">What can I help you with?</p>
      <div className="message-input">
        <div className="input-wrapper">
          <input
            type="text"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            placeholder="Enter your prompt here..."
          />
          <img 
            src={micIcon} 
            alt="Microphone" 
            className="mic-icon" 
            onClick={handleMicrophoneClick}
          />
          <img 
            src={sendIcon} 
            alt="Send" 
            className="send-icon" 
            onClick={handleSendMessage}
          />
        </div>
      </div>

      <QuickSuggestions onSuggestionClick={handleSuggestionClick} />

      {aiResponse && (
        <div className="ai-response">
          <p>{aiResponse}</p>
        </div>
      )}
    </div>
  );
};

export default ChatInterface;
