import React, { useEffect, useRef, useState } from 'react';
import '../styles/Sidebar.css';
import burgerIcon from '../assets/icons/burger.png';

function Sidebar({ isOpen, toggleSidebar, onManualSelect }) {
  const sidebarRef = useRef(null);
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploadStatus, setUploadStatus] = useState('');
  const [manuals, setManuals] = useState([]); 
  const [selectedManual, setSelectedManual] = useState('');

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file && file.type === 'application/pdf') {
      setSelectedFile(file);
      setUploadStatus(''); 
    } else {
      setUploadStatus('Please select a PDF file.');
    }
  };

  const handleFileUpload = async () => {
    if (!selectedFile) return;

    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const response = await fetch('/api/process_pdf', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        const result = await response.json();
        setUploadStatus('File uploaded successfully!');
        setManuals(prev => [...prev, result.file_name]); // Add new manual to the list
      } else {
        setUploadStatus('Failed to upload the file.');
      }
    } catch (error) {
      setUploadStatus('Error uploading file. Please try again.');
    }
  };

  // Fetch the list of available manuals from the backend
  useEffect(() => {
    const fetchManuals = async () => {
      const response = await fetch('/api/list_xml_files');
      const data = await response.json();
      setManuals(data.xml_files);
    };
    fetchManuals();
  }, []);

  return (
    <div className={`sidebar ${isOpen ? 'open' : ''}`} ref={sidebarRef}>
      <div className="sidebar-toggle" onClick={toggleSidebar}>
        <img src={burgerIcon} alt="Toggle Sidebar" />
      </div>
      <div className="sidebar-content">
        <h2>Upload PDF</h2>
        <input type="file" accept="application/pdf" onChange={handleFileChange} />
        {selectedFile && (
          <>
            <p>Selected File: {selectedFile.name}</p>
            <button onClick={handleFileUpload}>Upload PDF</button>
          </>
        )}
        <p>{uploadStatus}</p>

        {/* Manual Selection for AI Chat */}
        <h3>Select Manual for Reference</h3>
        <select 
          value={selectedManual} 
          onChange={(e) => {
            setSelectedManual(e.target.value);
            onManualSelect(e.target.value);
          }}>
          <option value="">Select a manual</option>
          {manuals.map((manual, index) => (
            <option key={index} value={manual}>{manual}</option>
          ))}
        </select>
      </div>
    </div>
  );
}

export default Sidebar;
